Column.Expression:
if(getconfigparm("QER\ComplianceCheck\SimpleMode") = "1")then(
if(0 = (from PWODecisionHistory select count() where UID_PersonWantsOrg = (select current uid_personwantsorg from PWOToDecide) and not(IsNullOrEmpty(UID_ComplianceRule))))then(
if(orderstate = 'OrderProduct')
then(
-- dependency on checkstatussemaphor to trigger recalculation
checkstatussemaphor>=0 and
not(exists("personwantsorg",
SqlAnd(
sqlcompareuid("UID_PersonWantsOrg",UID_PersonWantsOrg),
format("uid_personwantsorg in (select uid_personwantsorg from {0} where {1})",
sqlfunctioncalltable("CPL","GComplianceCheck", "FTCCSPWOCheck",sqlformatvalue(UID_PersonWantsOrg,"string", "NonUnicodeLiterals")),
sqlcompare("IsNewObject",true,"bool","equal")
))
)
)
)
else(true)
)
else(false)
)
else(true)

-- If this Configuration is disabled, the value will be 1 (the approval workflow will always show compliance messages)
if(not(convert(isnull(getconfig("VI_ITShop_Evaluate_ComplianceRelevance"),'false'), 'Boolean')))then(1)

-- If the ComplianceRelevance at decisionstep is > 0  than we will take it
else if(0 < (from QERWorkingStep select sum(isnull(ComplianceRelevance,0)) where UID_QERWorkingStep in (from PWOHelperPWO select UID_QERWorkingStep where UID_PersonWantsOrg = (select current UID_PersonWantsOrg from PWOToDecide)))) then(
   from QERWorkingStep select sum(isnull(ComplianceRelevance,0)) where UID_QERWorkingStep in (from PWOHelperPWO select UID_QERWorkingStep where UID_PersonWantsOrg = (select current UID_PersonWantsOrg from PWOToDecide))
)

-- for the following exceptions we will adjust the relevance to 1 and will see always a compliance message
else if(0 < (from QERWorkingStep select count() where UID_QERWorkingStep in (from PWOHelperPWO select UID_QERWorkingStep where UID_PersonWantsOrg = (select current UID_PersonWantsOrg from PWOToDecide)) and objectwalker("FK(UID_PWODecisionRule).decisionrule")+"" in ('OC','OA','OH')))then (1)

-- all other cases are not relevant
else (0)
