else
if(decisiontype="reset") then variable("%QBM_Primary_Blue%")
else
if(decisiontype="Escalate") then variable("%QBM_Primary_Red%")
else
if(decisiontype="reject") then variable("%QBM_Primary_Blue%")
else
if(decisiontype="grant") then variable("%QBM_Primary_Green%")
else
if(decisiontype="Create") then variable("%QBM_Primary_Yellow%")
else
if(decisiontype="Order") then variable("%QBM_Primary_Yellow%")
else
if(decisiontype="Reaction") then variable("%QBM_Primary_Violet%")
else
if(decisiontype="cancel") then variable("%VI_Common_Color_DarkerGray%")
else
if(decisiontype="prolongate") then variable("%QBM_Primary_Violet%")
else
if(decisiontype="query") then variable("%QBM_Primary_Orange%")
else
if(decisiontype="unsubscribe") then variable("%VI_Common_Color_DarkerGray%")
else
if(decisiontype="answer") then variable("%QBM_Primary_Orange%")
else
if(decisiontype="recall") then variable("%QBM_Primary_Red%")
else
